import React, { useState, useRef, useEffect } from 'react';
import { Send, AlertCircle } from 'lucide-react';
import axios from 'axios';

const ChatInterface = ({ conversationHistory, onNewMessage, apiToken, documentInfo, suggestedQuestion, onSuggestedQuestionUsed }) => {
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const messagesEndRef = useRef(null);

  // Set suggested question when it changes
  useEffect(() => {
    if (suggestedQuestion) {
      setInputMessage(suggestedQuestion);
    }
  }, [suggestedQuestion]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [conversationHistory]);

  const handleSendMessage = async () => {
    if (!inputMessage.trim() || isLoading) return;

    if (!apiToken) {
      setError('Please set your Hugging Face API token in Settings first!');
      return;
    }

    const question = inputMessage.trim();
    setInputMessage('');
    setIsLoading(true);
    setError('');

    // Clear suggested question after use
    if (suggestedQuestion && onSuggestedQuestionUsed) {
      onSuggestedQuestionUsed();
    }

    try {
      const response = await generateAIResponse(question, apiToken);
      onNewMessage(question, response);
    } catch (err) {
      setError(`Error getting AI response: ${err.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const generateAIResponse = async (question, token) => {
    try {
      // Create a simple, effective prompt
      const prompt = `Question: ${question}\n\nAnswer:`;

      // Try multiple reliable models with correct API format
      let response;
      const models = [
        {
          url: 'https://api-inference.huggingface.co/models/gpt2',
          name: 'GPT-2',
          params: { max_new_tokens: 100, temperature: 0.7, do_sample: true }
        },
        {
          url: 'https://api-inference.huggingface.co/models/facebook/opt-125m',
          name: 'OPT-125M',
          params: { max_new_tokens: 100, temperature: 0.7, do_sample: true }
        },
        {
          url: 'https://api-inference.huggingface.co/models/EleutherAI/gpt-neo-125M',
          name: 'GPT-Neo-125M',
          params: { max_new_tokens: 100, temperature: 0.7, do_sample: true }
        }
      ];

      let lastError;
      for (const model of models) {
        try {
          console.log(`Trying model: ${model.name} (${model.url})`);
          
          response = await axios.post(
            model.url,
            {
              inputs: prompt
            },
            {
              headers: {
                'Authorization': `Bearer ${token}`,
                'Content-Type': 'application/json'
              },
              timeout: 20000 // 20 second timeout per model
            }
          );
          
          console.log(`Success with model: ${model.name}`);
          break; // Exit loop if successful
          
        } catch (modelError) {
          console.log(`Failed with model ${model.name}:`, modelError.response?.status, modelError.message);
          lastError = modelError;
          
          if (modelError.response?.status === 401) {
            throw new Error('Invalid API token. Please check your Hugging Face API token in Settings.');
          }
          
          // Continue to next model if this one fails
          continue;
        }
      }

      // If all models failed
      if (!response) {
        console.error('All models failed. Last error:', lastError);
        throw new Error('All AI models are currently unavailable. Please try again in a few minutes or check your internet connection.');
      }

      console.log('API Response:', response.data); // Debug log

      // Handle different response formats
      let generatedText = '';
      
      if (response.data && Array.isArray(response.data) && response.data.length > 0) {
        // Standard format
        generatedText = response.data[0].generated_text || '';
      } else if (response.data && response.data.generated_text) {
        // Alternative format
        generatedText = response.data.generated_text;
      } else if (typeof response.data === 'string') {
        // Direct string response
        generatedText = response.data;
      } else {
        console.log('Unexpected response format:', response.data);
        throw new Error('Unexpected response format from AI model');
      }

      // Clean up the response
      let answer = generatedText.trim();
      
      // Remove the original prompt from the response
      if (answer.includes(prompt)) {
        answer = answer.replace(prompt, '').trim();
      }
      
      // If response is too short or empty, provide a helpful fallback
      if (answer.length < 10) {
        answer = `I understand you're asking about: "${question}". Based on the PDF content, I can help you find relevant information. Could you please ask a more specific question about what you'd like to know?`;
      }
      
      return answer;
      
    } catch (error) {
      console.error('API Error:', error); // Debug log
      
      if (error.response) {
        // Server responded with error status
        if (error.response.status === 401) {
          throw new Error('Invalid API token. Please check your Hugging Face API token in Settings.');
        } else if (error.response.status === 503) {
          throw new Error('AI models are currently loading. Please wait a moment and try again.');
        } else if (error.response.status === 429) {
          throw new Error('Rate limit exceeded. Please wait a moment before asking another question.');
        } else if (error.response.status === 404) {
          throw new Error('AI model endpoint not found. Please try again later.');
        } else {
          throw new Error(`API Error ${error.response.status}: ${error.response.statusText}`);
        }
      } else if (error.request) {
        // Request was made but no response received
        throw new Error('No response from AI service. Please check your internet connection and try again.');
      } else {
        // Something else happened
        throw new Error(error.message || 'Failed to connect to AI service. Please try again.');
      }
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-sm">
      {/* Chat Messages */}
      <div className="p-6 h-96 overflow-y-auto">
        {conversationHistory.length === 0 ? (
          <div className="text-center text-gray-500 py-8">
            <p>Start asking questions about your PDF document!</p>
          </div>
        ) : (
          <div className="space-y-4">
            {conversationHistory.map((message, index) => (
              <div key={index} className="animate-fade-in">
                {/* User Message */}
                <div className="flex justify-end mb-3">
                  <div className="bg-primary text-white px-4 py-2 rounded-2xl rounded-br-md max-w-xs lg:max-w-md">
                    {message.question}
                  </div>
                </div>
                
                {/* AI Response */}
                <div className="flex justify-start mb-3">
                  <div className="bg-gray-100 text-gray-800 px-4 py-2 rounded-2xl rounded-bl-md max-w-xs lg:max-w-md">
                    {message.answer}
                  </div>
                </div>
                
                {/* Timestamp */}
                <div className="text-xs text-gray-400 text-center mb-4">
                  {message.timestamp.toLocaleTimeString()}
                </div>
              </div>
            ))}
            
            {/* Loading indicator */}
            {isLoading && (
              <div className="flex justify-start mb-3">
                <div className="bg-gray-100 text-gray-800 px-4 py-2 rounded-2xl rounded-bl-md">
                  <div className="flex items-center gap-2">
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-primary"></div>
                    <span>AI is thinking...</span>
                  </div>
                </div>
              </div>
            )}
            
            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* Error Display */}
      {error && (
        <div className="px-6 pb-4">
          <div className="bg-red-50 border border-red-200 rounded-lg p-3 flex items-center gap-2">
            <AlertCircle className="text-red-500" size={16} />
            <span className="text-red-700 text-sm">{error}</span>
          </div>
        </div>
      )}

      {/* Input Section */}
      <div className="border-t p-4">
        <div className="flex gap-3">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Ask a question..."
            className="flex-1 px-4 py-2 border border-gray-300 rounded-full focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
            disabled={isLoading}
          />
          <button
            onClick={handleSendMessage}
            disabled={!inputMessage.trim() || isLoading}
            className="bg-primary hover:bg-primary/90 disabled:bg-gray-300 text-white px-6 py-2 rounded-full transition-colors flex items-center gap-2"
          >
            <Send size={16} />
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
