import React from 'react';

const SuggestedQuestions = ({ onQuestionSelect }) => {
  const suggestedQuestions = [
    "What are the main topics discussed in this document?",
    "What are the key findings or conclusions?",
    "Who are the main parties or stakeholders mentioned?",
    "What are the important dates or timelines?",
    "What are the main requirements or obligations?"
  ];

  const handleQuestionClick = (question) => {
    onQuestionSelect(question);
  };

  return (
    <div className="bg-white rounded-lg p-6 mb-6 shadow-sm">
      <h4 className="text-lg font-semibold text-gray-800 mb-4 text-center">
        ðŸ’¡ Suggested Questions:
      </h4>
      
      <div className="grid grid-cols-1 md:grid-cols-5 gap-3">
        {suggestedQuestions.map((question, index) => (
          <button
            key={index}
            onClick={() => handleQuestionClick(question)}
            className="bg-primary hover:bg-primary/90 text-white px-4 py-2 rounded-full text-sm font-medium transition-colors text-center hover:scale-105 transform duration-200"
          >
            {question.length > 30 ? `${question.substring(0, 30)}...` : question}
          </button>
        ))}
      </div>
      
      <p className="text-gray-600 text-sm text-center mt-3">
        Click any question above to get started with AI-powered analysis
      </p>
    </div>
  );
};

export default SuggestedQuestions;
