import React, { useState, useEffect } from 'react';
import PDFProcessor from './components/PDFProcessor';
import ChatInterface from './components/ChatInterface';
import SuggestedQuestions from './components/SuggestedQuestions';
import { Settings, Upload, FileText } from 'lucide-react';

function App() {
  const [pdfFile, setPdfFile] = useState(null);
  const [documentInfo, setDocumentInfo] = useState(null);
  const [conversationHistory, setConversationHistory] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [suggestedQuestion, setSuggestedQuestion] = useState('');

  const handleFileUpload = async (file) => {
    if (!file || !file.type.includes('pdf')) {
      alert('Please upload a valid PDF file.');
      return;
    }

    setPdfFile(file);
    setIsProcessing(true);

    try {
      const processor = new PDFProcessor();
      const result = await processor.processPDF(file);
      
      setDocumentInfo(result);
      setSuggestedQuestion(''); // Reset suggested question
      
      // Generate suggested questions based on the document
      const suggestions = generateSuggestedQuestions(result);
      setSuggestedQuestion(suggestions[0]); // Set first suggestion
      
    } catch (error) {
      console.error('Error processing PDF:', error);
      alert('Error processing PDF. Please try again.');
    } finally {
      setIsProcessing(false);
    }
  };

  const generateSuggestedQuestions = (docInfo) => {
    const baseQuestions = [
      "What is this document about?",
      "What are the main topics covered?",
      "Can you summarize the key points?",
      "What are the important conclusions?",
      "What recommendations are mentioned?"
    ];

    // Customize questions based on document length
    if (docInfo.totalPages > 50) {
      baseQuestions[0] = "What is the main purpose of this long document?";
      baseQuestions[1] = "What are the major sections and their content?";
    }

    if (docInfo.totalTextLength > 10000) {
      baseQuestions[2] = "Can you provide a comprehensive summary?";
    }

    return baseQuestions;
  };

  const handleNewMessage = (question, answer) => {
    const newMessage = {
      question,
      answer,
      timestamp: new Date()
    };
    setConversationHistory(prev => [...prev, newMessage]);
  };

  const handleSuggestedQuestionUsed = () => {
    setSuggestedQuestion(''); // Clear the suggested question after use
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center gap-3">
              <FileText className="text-primary" size={32} />
              <h1 className="text-2xl font-bold text-gray-900">AI PDF Q&A Assistant</h1>
            </div>
            <div className="flex items-center gap-2 px-4 py-2 text-green-600 bg-green-50 rounded-lg">
              <div className="w-2 h-2 bg-green-500 rounded-full"></div>
              <span className="text-sm font-medium">Gemini API Ready</span>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {!documentInfo ? (
          /* Upload Interface */
          <div className="max-w-2xl mx-auto">
            <div className="bg-white rounded-lg shadow-sm p-8 text-center">
              <Upload className="text-primary mx-auto mb-4" size={48} />
              <h2 className="text-2xl font-semibold text-gray-900 mb-2">Upload Your PDF Document</h2>
              <p className="text-gray-600 mb-6">
                Upload a PDF document to start asking questions and get AI-powered answers using Google Gemini.
              </p>
              
              <div className="space-y-4">
                <input
                  type="file"
                  accept=".pdf"
                  onChange={(e) => handleFileUpload(e.target.files[0])}
                  className="hidden"
                  id="pdf-upload"
                  disabled={isProcessing}
                />
                <label
                  htmlFor="pdf-upload"
                  className="inline-flex items-center gap-2 px-6 py-3 bg-primary text-white rounded-lg hover:bg-primary/90 transition-colors cursor-pointer disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {isProcessing ? (
                    <>
                      <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
                      Processing PDF...
                    </>
                  ) : (
                    <>
                      <Upload size={20} />
                      Choose PDF File
                    </>
                  )}
                </label>
              </div>

              {isProcessing && (
                <div className="mt-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
                  <div className="flex items-center gap-3">
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-primary"></div>
                    <div>
                      <p className="text-blue-800 font-medium">Processing your PDF...</p>
                      <p className="text-blue-600 text-sm">This may take a few moments for large documents.</p>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        ) : (
          /* Chat Interface */
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            {/* Document Info */}
            <div className="lg:col-span-1">
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center gap-2">
                  <FileText size={20} />
                  Document Information
                </h3>
                <div className="space-y-3 text-sm text-gray-600">
                  <div className="flex justify-between">
                    <span>File Name:</span>
                    <span className="font-medium text-gray-900">{pdfFile?.name}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Pages:</span>
                    <span className="font-medium text-gray-900">{documentInfo.totalPages}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Text Chunks:</span>
                    <span className="font-medium text-gray-900">{documentInfo.totalChunks}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Total Text:</span>
                    <span className="font-medium text-gray-900">
                      {Math.round(documentInfo.totalTextLength / 1000)}K characters
                    </span>
                  </div>
                </div>
              </div>

              {/* Suggested Questions */}
              <div className="mt-6">
                <SuggestedQuestions
                  onQuestionSelect={setSuggestedQuestion}
                  documentInfo={documentInfo}
                />
              </div>
            </div>

            {/* Chat Interface */}
            <div className="lg:col-span-2">
              <ChatInterface
                conversationHistory={conversationHistory}
                onNewMessage={handleNewMessage}
                documentInfo={documentInfo}
                suggestedQuestion={suggestedQuestion}
                onSuggestedQuestionUsed={handleSuggestedQuestionUsed}
              />
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
