import React, { useState, useRef, useEffect } from 'react';
import { Upload, FileText, Send, Settings, X, CheckCircle, AlertCircle } from 'lucide-react';
import PDFProcessor from './components/PDFProcessor';
import ChatInterface from './components/ChatInterface';
import SettingsPanel from './components/SettingsPanel';
import SuggestedQuestions from './components/SuggestedQuestions';

function App() {
  const [pdfProcessed, setPdfProcessed] = useState(false);
  const [documentInfo, setDocumentInfo] = useState({});
  const [conversationHistory, setConversationHistory] = useState([]);
  const [showSettings, setShowSettings] = useState(false);
  const [apiToken, setApiToken] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState('');
  const [suggestedQuestion, setSuggestedQuestion] = useState('');

  const fileInputRef = useRef(null);

  // Load API token from localStorage
  useEffect(() => {
    const savedToken = localStorage.getItem('hf_api_token');
    if (savedToken) {
      setApiToken(savedToken);
    }
  }, []);

  // Save API token to localStorage
  useEffect(() => {
    if (apiToken) {
      localStorage.setItem('hf_api_token', apiToken);
    }
  }, [apiToken]);

  const handleFileUpload = async (file) => {
    if (!file || file.type !== 'application/pdf') {
      setError('Please select a valid PDF file');
      return;
    }

    if (file.size > 10 * 1024 * 1024) { // 10MB limit
      setError('File size must be less than 10MB');
      return;
    }

    setIsProcessing(true);
    setError('');

    try {
      const processor = new PDFProcessor();
      const result = await processor.processPDF(file);
      
      setDocumentInfo({
        fileName: file.name,
        totalPages: result.totalPages,
        totalChunks: result.totalChunks
      });
      
      setPdfProcessed(true);
    } catch (err) {
      setError(`Error processing PDF: ${err.message}`);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];
    if (file) {
      handleFileUpload(file);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const addToConversation = (question, answer) => {
    setConversationHistory(prev => [...prev, { question, answer, timestamp: new Date() }]);
  };

  const handleSuggestedQuestion = (question) => {
    setSuggestedQuestion(question);
  };

  if (!pdfProcessed) {
    return (
      <div className="min-h-screen bg-gray-50">
        {/* Header */}
        <div className="bg-white shadow-sm border-b">
          <div className="max-w-4xl mx-auto px-4 py-4 flex justify-between items-center">
            <h1 className="text-2xl font-bold text-gray-800">Logo</h1>
            <button
              onClick={() => setShowSettings(true)}
              className="p-2 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-lg transition-colors"
            >
              <Settings size={20} />
            </button>
          </div>
        </div>

        {/* Main Content */}
        <div className="max-w-4xl mx-auto px-4 py-8">
          {/* Upload Section */}
          <div className="bg-white rounded-lg border-2 border-dashed border-gray-300 p-12 text-center hover:border-primary transition-colors">
            <div className="text-6xl text-gray-400 mb-4">ðŸ“„</div>
            <h2 className="text-2xl font-bold text-gray-800 mb-2">Upload PDF</h2>
            <p className="text-gray-600 mb-6">(Accept only PDF. Max size: 10 MB)</p>
            
            <input
              ref={fileInputRef}
              type="file"
              accept=".pdf"
              onChange={(e) => handleFileUpload(e.target.files[0])}
              className="hidden"
            />
            
            <button
              onClick={() => fileInputRef.current?.click()}
              className="bg-primary hover:bg-primary/90 text-white px-6 py-3 rounded-lg font-medium transition-colors"
            >
              Choose File
            </button>
            
            <div
              className="mt-4 p-4 border-2 border-dashed border-gray-300 rounded-lg"
              onDrop={handleDrop}
              onDragOver={handleDragOver}
            >
              <p className="text-gray-500">Or drag and drop your PDF here</p>
            </div>
          </div>

          {/* Error Display */}
          {error && (
            <div className="mt-4 bg-red-50 border border-red-200 rounded-lg p-4 flex items-center gap-2">
              <AlertCircle className="text-red-500" size={20} />
              <span className="text-red-700">{error}</span>
            </div>
          )}

          {/* Processing Indicator */}
          {isProcessing && (
            <div className="mt-4 bg-blue-50 border border-blue-200 rounded-lg p-4 flex items-center gap-2">
              <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-blue-600"></div>
              <span className="text-blue-700">Processing PDF...</span>
            </div>
          )}
        </div>

        {/* Settings Panel */}
        {showSettings && (
          <SettingsPanel
            apiToken={apiToken}
            setApiToken={setApiToken}
            onClose={() => setShowSettings(false)}
          />
        )}
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-4xl mx-auto px-4 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-gray-800">Logo</h1>
          <button
            onClick={() => setShowSettings(true)}
            className="p-2 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <Settings size={20} />
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-4xl mx-auto px-4 py-8">
        {/* Status Section */}
        <div className="bg-white rounded-lg p-6 mb-6 border-l-4 border-success">
          <div className="flex items-center gap-3 mb-2">
            <CheckCircle className="text-success" size={20} />
            <span className="font-medium">PDF: {documentInfo.fileName}</span>
          </div>
          <div className="text-gray-600">
            <strong>Pages:</strong> {documentInfo.totalPages} Uploaded successfully
          </div>
        </div>

        {/* Suggested Questions */}
        <SuggestedQuestions onQuestionSelect={handleSuggestedQuestion} />

        {/* Chat Interface */}
        <ChatInterface
          conversationHistory={conversationHistory}
          onNewMessage={addToConversation}
          apiToken={apiToken}
          documentInfo={documentInfo}
          suggestedQuestion={suggestedQuestion}
          onSuggestedQuestionUsed={() => setSuggestedQuestion('')}
        />
      </div>

      {/* Settings Panel */}
      {showSettings && (
        <SettingsPanel
          apiToken={apiToken}
          setApiToken={setApiToken}
          onClose={() => setShowSettings(false)}
        />
      )}
    </div>
  );
}

export default App;
